using Microsoft.VisualStudio.TestTools.UnitTesting;
using leetcode_CSharp;
using System;
using System.Collections.Generic;
using System.Linq;
using System.Text;
using System.Threading.Tasks;

namespace leetcode_CSharp.Tests
{
    [TestClass()]
    public class WildcardMatchingTests
    {
        [TestMethod()]
        public void IsMatchTest()
        {
            Action<string, string, bool> t = (a1, a2, r) =>
                    Assert.AreEqual(r, new WildcardMatching().IsMatch(a1, a2));
            t("aa", "a", false);
            t("aa", "aa", true);
            t("aaa", "aa", false);
            t("aa", "*", true);
            t("aa", "a*", true);
            t("a", "a*", true);
            t("a", "", false);
            t("ab", "?*", true);
            t("aab", "c*a*b", false);
            t("",
"**", false);
            t("", "*", true);
            t("ab", "*?*?*", true);
            t("aaba", "*b**?", true);
        }
    }
}